INSERT INTO `detail_product` VALUES (4,4,'Gối vân xám',100000,'<p>H&atilde;y chọn chất liệu gối ph&ugrave; hợp với sở th&iacute;ch v&agrave; nhu cầu của bạn. Nếu bạn th&iacute;ch cảm gi&aacute;c mềm mại, h&atilde;y chọn gối b&ocirc;ng g&ograve;n hoặc cao su. Nếu bạn cần độ đ&agrave;n hồi tốt, h&atilde;y chọn gối cao su hoặc xốp. Nếu bạn muốn cảm gi&aacute;c tho&aacute;ng m&aacute;t, h&atilde;y chọn gối l&ocirc;ng vũ.</p>\r\n','goi-vuong-van-go-1.jpg',120000,120,1,1),(5,4,'Gối mỏ leo xanh xẫm',135000,'<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Gối được l&agrave;m từ nhiều chất liệu kh&aacute;c nhau như b&ocirc;ng g&ograve;n, cao su, l&ocirc;ng vũ, xốp,... Mỗi loại chất liệu đều c&oacute; những ưu điểm v&agrave; nhược điểm ri&ecirc;ng. Gối b&ocirc;ng g&ograve;n mềm mại, &ecirc;m &aacute;i v&agrave; c&oacute; gi&aacute; th&agrave;nh rẻ. Gối cao su c&oacute; độ đ&agrave;n hồi tốt, gi&uacute;p n&acirc;ng đỡ cột sống cổ v&agrave; vai g&aacute;y. Gối L&ocirc;ng vũ nhẹ nh&agrave;ng, tho&aacute;ng m&aacute;t v&agrave; mang lại cảm gi&aacute;c sang trọng. Gối xốp c&oacute; độ bền cao v&agrave; dễ d&agrave;ng vệ sinh.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n','goi-vuong-mo-leo-2.jpg',140000,105,1,1),(6,4,'Gối hình tròn đan xen',90000,'kích cỡ: 50x50, vỏ cotton, ruột bông nhân tạo.Gối có nhiều hình dạng và kích thước khác nhau, phù hợp với tư thế ngủ và sở thích của mỗi người. Gối vuông thường được sử dụng để kê đầu khi ngủ ngửa. Gối chữ nhật phù hợp cho những người ngủ nghiêng. Gối ôm giúp bạn cảm thấy thoải mái và an toàn hơn khi ngủ. Gối du lịch nhỏ gọn, tiện lợi mang theo khi đi du lịch hoặc công tác.  Ngoài ra, gối còn có nhiều họa tiết và màu sắc khác nhau để bạn lựa chọn, giúp tô điểm thêm cho không gian phòng ngủ của bạn.','goi-vuong-soc-tron-3.jpg',125000,36,1,2),(7,4,'Gối ngọc bích',150000,'kích cỡ: 50x50, vỏ nhung, ruột bông nhân tạo.Gối là vật dụng không thể thiếu trong cuộc sống mỗi người, góp phần mang đến cho bạn giấc ngủ ngon và thoải mái. Gối có nhiều loại khá